import "./App.css";
import { useState } from "react";
export default function App() {
  const [page, setPage] = useState("About Me");
  function handleClick(e) {
    e.stopPropagation();
    setPage(e.target.innerHTML);
  }

  function renderIt() {
    switch (page) {
      case "About Me":
        return `<AboutMe />`;
      case "Portfolio":
        break;
      default:
        break;
    }
  }
  const renderPage = renderIt();

  return (
    <div className="App">
      <Header handleClick={handleClick} />
      <section>
        <AboutMe myPage={page} />
      </section>
    </div>
  );
}

function Header({ handleClick }) {
  return (
    <header>
      <h1>Daniel Cross</h1>
      <Nav handleClick={handleClick} />
    </header>
  );
}

function Nav({ handleClick }) {
  return (
    <nav>
      <ul>
        <li onClick={handleClick}>About Me</li>
        <li onClick={handleClick}>Portfolio</li>
        <li onClick={handleClick}>Contact</li>
        <li onClick={handleClick}>Resumé</li>
      </ul>
    </nav>
  );
}

function Project() {
  return (
    <div>
      <img src={"public/logo512.png"} alt="react-logo" />
    </div>
  );
}

function AboutMe() {
  return (
    <div>
      <h1>About Me</h1>
    </div>
  );
}
function Portfolio() {
  return (
    <div>
      <h1>Portfolio</h1>
    </div>
  );
}
function Contact() {
  return (
    <div>
      <h1>Contact</h1>
    </div>
  );
}
function Resume() {
  return (
    <div>
      <h1>Resumé</h1>
    </div>
  );
}
