import "./App.css";
import { useState } from "react";
import { Outlet, Link } from "react-router-dom";
// import radix components
import { Flex, Box, Text, Heading, Button } from "@radix-ui/themes";

export default function App() {
  return (
    <div className="App">
      <Header />
      <section>
        <Outlet />
      </section>
      <Footer />
    </div>
  );
}

function Header(props) {
  console.log(props);
  return (
    <Flex direction="row" justify="between" m="4">
      <Heading>Daniel Cross</Heading>
      <Nav />
    </Flex>
  );
}

function Nav({ tabColor }) {
  const [currentTab, setCurrentTab] = useState("1");
  const activeTab = "red";
  function handleTabClick(e) {
    e.target.attributes[0].value = "red";
  }
  console.log(props);
  return (
    <nav>
      <Flex direction="row" gap="2">
        <Link
          onClick={handleTabClick}
          key={1}
          className="nav-link text-light"
          to="/about"
        >
          <Button color={tabColor}>About Me</Button>
        </Link>

        <Link
          onClick={handleTabClick}
          key={2}
          className="nav-link text-light"
          to="/portfolio"
        >
          <Button>Portfolio</Button>
        </Link>

        <Link
          onClick={handleTabClick}
          key={3}
          className="nav-link text-light"
          to="/contact"
        >
          <Button>Contact</Button>
        </Link>

        <Link
          onClick={handleTabClick}
          key={4}
          className="nav-link text-light"
          to="/resume"
        >
          <Button>Resum√©</Button>
        </Link>
      </Flex>
    </nav>
  );
}

function Footer() {
  return <footer>Here is the footer</footer>;
}
