import "./App.css";
import { useState } from "react";
import { Outlet, Link } from "react-router-dom";
// import radix components
import { Flex, Box, Text, Heading, Button } from "@radix-ui/themes";

export default function App() {
  return (
    <div className="App">
      <Header />
      <section>
        <Outlet />
      </section>
      <Footer />
    </div>
  );
}

function Header(props) {
  const [currentTab, setCurrentTab] = useState("1");
  function handleTabClick(tabNum) {
    setCurrentTab(tabNum);
  }
  console.log(props);
  return (
    <Flex direction="row" justify="between" m="4">
      <Heading>Daniel Cross</Heading>
      <Nav tabColor="red" currentTab={currentTab} onClick={handleTabClick} />
    </Flex>
  );
}

function Nav({ tabColor, currentTab, onClick }) {
  console.log(currentTab);
  return (
    <nav>
      <Flex direction="row" gap="2">
        <Link
          onClick={() => onClick(1)}
          key={1}
          className="nav-link text-light"
          to="/about"
        >
          <Button color={currentTab === 1 ? "red" : "iris"}>About Me</Button>
        </Link>

        <Link
          onClick={() => onClick(2)}
          key={2}
          className="nav-link text-light"
          to="/portfolio"
        >
          <Button color={currentTab === 2 ? "red" : "iris"}>Portfolio</Button>
        </Link>

        <Link
          onClick={() => onClick(3)}
          key={3}
          className="nav-link text-light"
          to="/contact"
        >
          <Button color={currentTab === 3 ? "red" : "iris"}>Contact</Button>
        </Link>

        <Link
          onClick={() => onClick(4)}
          key={4}
          className="nav-link text-light"
          to="/resume"
        >
          <Button color={currentTab === 4 ? "red" : "iris"}>Resum√©</Button>
        </Link>
      </Flex>
    </nav>
  );
}

function Footer() {
  return <footer>Here is the footer</footer>;
}
